          {
             parentenable = "";   
             parentenableMessage = "Disabled";  
          } 
          $("#parentPass").removeClass('addredborder');    
          var parentPass = $("#parentPass").val();
          if(parentPass == "")
          {
            $("#parentPass").addClass('addredborder');
          }
          else
