            type:"POST",
            url:"includes/ajax-control.php",
            dataType:"text",
            data:{
              action:'confirm_parentpassword',
              parentPass:parentPass
            },  
              success:function(response2){ 
                $('#checkingprocess3').addClass('hideOnLoad');
                 if(response2 != 0)
                 {
                    var savedparentpassword = "";
                    var live_player = $("#live_player");
